mapper.insert:
INSERT INTO examboard (
	board_seq,
	reg_id,
	Board_content,
	Board_pw,
	Board_dt,
	Board_updt,
	Board_title
	) VALUES (
	'seed'||TO_CHAR(NOW(),'yyyymmdd')||LPAD(NEXTVAL('seq_board_num')::TEXT,8,'0')
	,#{reg_id}
	,#{board_content}
	,#{board_pw}
	,transaction_timestamp()
	,transaction_timestamp()
	,#{board_title}
	)
